import React from 'react';
import axios from 'axios';
import * as style from './styled'

const Home = ({ title }) => {
  const [user, setUser] = React.useState('')

  async function handleSearch() {
    let response = await axios.get(`http://api.github.com/users/${user}/repos`)
    console.log(response.data)
  }

  return (
    <div>
      <h1>{title}</h1>
      <p><em>Insert the user you want to look for.</em></p>
      <input className="userInput" placeholder="username" value={user} onChange={event => setUser(event.target.value)} />
      <button type="button" onClick={handleSearch}>GO!</button>
    </div>
  );
}

export default Home
